import React, { Component } from 'react';
import { getAllFiles } from './api';
import { List, message, Typography, Button } from 'antd';
import './listfiles.css';
import Filters from './Filters';
import ListFileItem from './ListFileItem';
import { angleDoubleUp } from 'react-icons-kit/fa/angleDoubleUp';
import { Icon } from 'react-icons-kit';

const { Text } = Typography;

class ListFiles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      loading: true,
      homeDirectory: '/',
      currentDirectory: '/',
    };
  }

  goBack = () => {
    console.log('GO BACK GO BACK', this.props);
    this.props.history.goBack();
  };

  folderClicked = (path) => {
    this.getData({ directory: path });
  };
  componentDidMount() {
    const {
      location: { pathname },
    } = this.props;

    this.getData({ directory: pathname });
  }

  getData = (args) => {
    this.setState({ loading: true });
    getAllFiles(args)
      .then((response) => {
        console.log(response);
        this.setState({ data: response.data });
      })
      .catch((error) => {
        console.log(error);
        message.error('Failed to fetch data');
      })
      .finally(() => {
        this.setState({ loading: false });
      });
  };

  render() {
    const { loading, data } = this.state;
    console.log(this.props);
    const {
      location: { pathname },
    } = this.props;
    return (
      <>
        <div className='list-container'>
          <Filters data={data}>
            {({ files }) => {
              return (
                <List
                  size='small'
                  header={
                    <div>
                      <Button
                        shape='circle'
                        onClick={this.goBack}
                        icon={<Icon icon={angleDoubleUp} />}
                        style={{ marginRight: '5px' }}
                      />

                      <Text type='secondary'>
                        Current Directory: {pathname}
                      </Text>
                    </div>
                  }
                  bordered={true}
                  loading={loading}
                  dataSource={files}
                  locale={{ emptyText: 'Directory is empty' }}
                  renderItem={(item) => <ListFileItem item={item} />}
                />
              );
            }}
          </Filters>
        </div>
      </>
    );
  }
}

export default ListFiles;
